/* eslint-disable import/no-anonymous-default-export */
import React, {useRef} from 'react';
import Styled from 'styled-components';
import Picture from './Picture';
import Masonry from 'https://unpkg.com/masonry-layout@4.2.2/dist/masonry.pkgd.min.js';

export default ({list}) => {

    const listWrap = useRef(null);

    const elem = listWrap;
    const msnry = new Masonry(elem, {
        
    })

    return (
        <PictureList id="pictureListWrap" ref={listWrap}>
            {list.map((data, idx) => {
                return <Picture data={data} key={idx} />
            })}
        </PictureList>
    )
}

const PictureList = Styled.section`
    width: 100%;
    text-align: center;
    position: relative;
    display: flex;
    flex-wrap: wrap
`;

